mapper.select:
select e.*,tp.descripcion_largo as descripcion_tipo_comprobante, top.descripcion_largo as descripcion_tipo_operacion, cl.*, dom.*, em.* from t_comprobante e inner join t_tabla_tablas_detalle tdi on cast (e.tipo_docu_iden_cab as varchar) = tdi.codigo_catalogo and tdi.id_maestra=6 inner join t_cliente cl on cl.id_cliente=e.id_cliente inner join t_tabla_tablas_detalle tp on e.tipo_comprobante = tp.codigo_catalogo and tp.id_maestra=1 inner join t_tabla_tablas_detalle top on cast ( e.tipo_operacion_cab as int)= cast (top.codigo_catalogo as int )and top.id_maestra=51 inner join t_domicilio_fiscal dom on dom.id_domicilio_fiscal_cab=e.id_domicilio_fiscal_cab inner join t_emisor em on em.id_emisor=e.id_emisor where e.tipo_comprobante=#{tipo_comprobante} and extract(month from e.fecha_emision_cab)= #{mes} and extract(year from e.fecha_emision_cab)=#{anio}
and ( e.id_cliente like ('%' || replace( #{item.value}, ' ', '%') || '%') )
and ${item.key} like ('%' || replace( #{item.value}, ' ', '%') || '%')
order by ${sortField} ${sortOrder}
OFFSET #{first} LIMIT #{pageSize}
